function getColor() {
  const R = Math.round(Math.random() * 255)
    .toString(16)
    .padStart(2, "0");

  const G = Math.round(Math.random() * 255)
    .toString(16)
    .padStart(2, "0");

  const B = Math.round(Math.random() * 255)
    .toString(16)
    .padStart(2, "0");

  return `#${R}${G}${B}`;
}

document.querySelector("circle").addEventListener("click", (e) => {
  e.target.style.fill = getColor();
});
